struct Node(Option<RefCell<Rc<Node>>>); let a = Rc::new(Node(Some(RefCell::new(Rc::new(Node(None))))));
let b = Rc::new(Node(Some(RefCell::new(a.clone()))));
if let Some(r) = &a.0 { *r.borrow_mut() = b.clone();
}
println!("{:?}", a);	// 会导致栈溢出 一种解决方案是重新组织数据结构，使得一部分引用拥有所有权而另一部分没有。具体参考 the book

