fn add(self, other: &str) -> String let s1 = "foo".to_string();
let s2 = "bar".to_string();
let s = s1 + "-" + &s2; // s1 不再可用，s2 仍有效 format! 宏 format! 宏的工作原理与 print! 相同。format! 取参数的引用，不获取所有权。 let s1 = "foo".to_string();
let s2 = "bar".to_string();
